pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/cryptography/ECDSA.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";

import "../../rootLogic/contracts/RootLogic.sol";
import "../../licensedUser/contracts/LicensedUserLogic.sol";
import "../../licensedUser/contracts/IdentityUserType.sol";
import "../../name/contracts/Contracts.sol";

contract BulkIssue is IdentityUserType, Contracts, OwnableUpgradeSafe {
  using ECDSA for bytes32;
  /*
   * Structs
   */
  struct Batch {
    bool prepared;
    address issuer;
    uint8 processedCount;
    mapping(bytes32 => bool) processedLeaves;
  }

  /*
   * Events
   */
  event BatchPrepared(bytes32 indexed merkleRoot);

  event CreateSRRWithProof(bytes32 indexed merkleRoot, bytes32 srrHash);

  /*
   * Storages
   */
  mapping(bytes32 => Batch) public batches;
  address private _nameRegistryAddress;

  /*
   * Modifiers
   */
  modifier isNewBatch(bytes32 merkleRoot) {
    require(
      !batches[merkleRoot].prepared,
      "Batch already exists for the given merkle root"
    );
    _;
  }

  modifier batchExists(bytes32 merkleRoot) {
    require(
      batches[merkleRoot].prepared,
      "Batch doesn't exist for the given merkle root"
    );
    _;
  }

  modifier srrNotAlreadyProcessed(bytes32 merkleRoot, bytes32 srrHash) {
    require(
      !batches[merkleRoot].processedLeaves[srrHash],
      "SRR already processed."
    );
    _;
  }

  modifier onlyLicensedUser() {
    require(
      _nameRegistry().instantiation(msg.sender) || _isAdmin(),
      "The sender is neither the licensed user contract nor the admin"
    );
    _;
  }

  /*
   * Public functions
   */

  /**
   * @dev Initializes the BulkIssue contract
   */
  function initialize(address nameRegistry) public initializer {
    _nameRegistryAddress = nameRegistry;
    OwnableUpgradeSafe.__Ownable_init();
  }

  /**
   * @dev Reserves a batch request before excuting
   * @param merkleRoot bytes32 of merkle root
   */
  function prepareBatch(bytes32 merkleRoot)
    public
    isNewBatch(merkleRoot)
    onlyOwner
  {
    _prepareBatch(merkleRoot);
  }

  /**
   * @dev Reserves a batch request before excuting
   * @param merkleRoot bytes32 of merkle root
   * @param v uint8 of sig.v
   * @param r bytes32 of sig.r
   * @param s bytes32 of sig.s
   */
  function prepareBatchFromLicensedUser(
    bytes32 merkleRoot,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) public isNewBatch(merkleRoot) onlyLicensedUser {
    bytes32 messageHash = merkleRoot.toEthSignedMessageHash();
    address signer = ecrecover(messageHash, v, r, s);
    require(
      LicensedUserLogic(msg.sender).isOwnerAddress(signer),
      "Signer must be an owner on the calling LicensedUserWallet"
    );
    _prepareBatch(merkleRoot);
  }

  /**
   * @dev Creates SRR with merkle proof
   * @param merkleProof bytes32 of merkle proof value
   * @param merkleRoot bytes32 of merkle root value
   * @param srrHash bytes32 of merkle tree leaf
   * @param isPrimaryIssuer boolean whether the user is a primary issuer
   * @param artistAddress address of the artist contract
   * @param metadataDigest bytes32 of metadata hash
   */
  function createSRRWithProof(
    bytes32[] memory merkleProof,
    bytes32 merkleRoot,
    bytes32 srrHash,
    bool isPrimaryIssuer,
    address artistAddress,
    bytes32 metadataDigest
  )
    public
    batchExists(merkleRoot)
    srrNotAlreadyProcessed(merkleRoot, srrHash)
    returns (uint256 tokenId)
  {
    require(
      verifySRRHash(srrHash, isPrimaryIssuer, artistAddress, metadataDigest),
      "srrHash does not match the srr details"
    );

    require(
      MerkleProof.verify(merkleProof, merkleRoot, srrHash),
      "Merkle proof verification failed"
    );

    batches[merkleRoot].processedLeaves[srrHash] = true;
    batches[merkleRoot].processedCount++;

    tokenId = _rootLogic().createSRRFromBulk(
      isPrimaryIssuer,
      artistAddress,
      metadataDigest,
      batches[merkleRoot].issuer // LUW address - issuer
    );
    emit CreateSRRWithProof(merkleRoot, srrHash);
  }

  /**
   * @dev Verify the hash of the params when creating SRR
   * @param srrHash bytes32 of hash value of all other params
   * @param isPrimaryIssuer boolean whether the user is a primary issuer
   * @param artistAddress address of the artist contract
   * @param metadataDigest bytes32 of metadata hash
   */
  function verifySRRHash(
    bytes32 srrHash,
    bool isPrimaryIssuer,
    address artistAddress,
    bytes32 metadataDigest
  ) public pure returns (bool hashMatches) {
    hashMatches =
      srrHash ==
      keccak256(
        abi.encodePacked(isPrimaryIssuer, artistAddress, metadataDigest)
      );
  }

  /**
   * @dev Gets the endpoint address of NameRegistry
   * @return NameRegistry proxy address
   */
  function getNameRegistryAddress() public view returns (address) {
    return _nameRegistryAddress;
  }

  /**
   * Private functions
   */

  function _prepareBatch(bytes32 merkleRoot) private {
    batches[merkleRoot] = Batch({
      prepared: true,
      issuer: msg.sender,
      processedCount: 0
    });
    emit BatchPrepared(merkleRoot);
  }

  function _isAdmin() private view returns (bool) {
    address proxy = msg.sender;
    LicensedUserLogic logic = LicensedUserLogic(proxy);
    return logic.getUserType() == UserType.ADMIN;
  }

  function _nameRegistry() private view returns (NameRegistry) {
    return NameRegistry(_nameRegistryAddress);
  }

  function _rootLogic() private view returns (RootLogic) {
    address rootLogicAddress = _nameRegistry().get(ROOT_LOGIC);
    RootLogic rootLogic = RootLogic(rootLogicAddress);
    return rootLogic;
  }
}

