pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "../../common/IRootLogic.sol";
import "../../lib/contracts/Signature.sol";
import "../../common/IStartrailRegistry.sol";
import "../../common/IStartrailValidator.sol";
import "../../licensedUser/contracts/LicensedUserLogic.sol";
import "../../licensedUser/contracts/IdentityUserType.sol";
import "../../name/contracts/NameRegistry.sol";
import "../../name/contracts/Contracts.sol";
import "./Storage.sol";
import "./ERC2477.sol";

contract RootLogic is
  IRootLogic,
  IStartrailValidator,
  IdentityUserType,
  Contracts,
  ERC2477,
  Storage,
  OwnableUpgradeSafe
{
  /*
   * Constants
   */
  // Static
  uint256 private constant SRR_GLOBAL_SLOT = 0;
  // types
  bytes32 private constant _SRR = keccak256("registryRecord");
  bytes32 private constant _HISTORY = keccak256("historyProvenance");

  // values
  bytes32 private constant _IS_PRIMARY_ISSUER = keccak256("isPrimaryIssuer");
  bytes32 private constant _ISSUER = keccak256("issuer");
  bytes32 private constant _ARTIST_ADDRESS = keccak256("artistAddress");
  bytes32 private constant _ROYALTY_RATE = keccak256("royaltyRate");
  bytes32 private constant _ROYALTY_RATE_DECIMALS = keccak256(
    "royaltyRateDecimals"
  );
  bytes32 private constant _COMMITMENT = keccak256("commitment");
  bytes32 private constant _NAME_REGISTRY = keccak256("nameRegistryAddress");

  // metadata
  bytes32 private constant _URI_PREFIX = keccak256("URIPrefix");
  bytes32 private constant _URI_POSTFIX = keccak256("URIPostfix");
  bytes32 private constant _SCHEMA_URI_DIGEST = keccak256("schemaURIDigest");
  bytes32 private constant _METADATA_DIGEST = keccak256("metadataDigest");
  // bytes32 private constant _PLATFORM_EOA = keccak256("platformEOA");

  /*
   * Hashing function.
   */
  string public constant HASH_ALGORITHM = "sha256";

  /*
   * Modifiers
   */
  modifier onlyAdmin() {
    require(_isAdmin(), "The sender is not the admin");
    _;
  }

  modifier onlyLicensedUser() {
    require(
      _nameRegistry().instantiation(msg.sender),
      "The sender is neither the licensed user contract nor the admin"
    );
    _;
  }

  modifier onlyBulk() {
    address bulkIssueAddress = _nameRegistry().get(BULK_ISSUE);
    require(
      bulkIssueAddress == msg.sender,
      "The sender isn't the bulk issue contract"
    );
    _;
  }

  modifier onlyArtist(uint256 tokenId) {
    require(
      msg.sender == _addressStorage[tokenId][_SRR][_ARTIST_ADDRESS] ||
        _isAdmin(),
      "This is neither a artist nor the admin"
    );
    _;
  }

  modifier onlyPrimaryIssuer(uint256 tokenId) {
    require(
      (msg.sender == _addressStorage[tokenId][_SRR][_ISSUER] &&
        _boolStorage[tokenId][_SRR][_IS_PRIMARY_ISSUER]) || _isAdmin(),
      "This is neither a primary issuer nor the admin"
    );
    _;
  }

  modifier onlySRROwner(uint256 tokenId) {
    address owner = getSRROwner(tokenId);
    require(
      owner == msg.sender || _isAdmin(),
      "Sender is neither a SRR owner nor the admin"
    );
    _;
  }

  modifier tokenExists(uint256 tokenId) {
    require(_exists(tokenId), "The tokenId does not exist");
    _;
  }

  /**
   * @dev Initializes the address of the StartrailRegistry contract
   * @param nameRegistry address of the NameRegistry
   * @param URIPrefix string of the URI prefix of the scheme where SRR metadata is saved
   * @param URIPostfix string of the URI postfix of the scheme
   * @param schemaURIIntegrityDigest bytes32 of the URI digest of the scheme
   */
  function initialize(
    address nameRegistry,
    string memory URIPrefix,
    string memory URIPostfix,
    bytes32 schemaURIIntegrityDigest
  ) public initializer {
    _addressStorage[SRR_GLOBAL_SLOT][_NAME_REGISTRY][_NAME_REGISTRY] = nameRegistry;
    _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_PREFIX] = URIPrefix;
    _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_POSTFIX] = URIPostfix;
    _bytes32Storage[SRR_GLOBAL_SLOT][_SRR][_SCHEMA_URI_DIGEST] = schemaURIIntegrityDigest;
    OwnableUpgradeSafe.__Ownable_init();
  }

  function supportsInterface(bytes4 interfaceID) external pure returns (bool) {
    return
      // bytes4(keccak256('supportsInterface(bytes4)')) == 0x01ffc9a7
      interfaceID == 0x01ffc9a7 || // ERC165

      /*
        *   bytes4(keccak256('tokenURIIntegrity(uint256)')) == 0x49846680
        *   bytes4(keccak256('tokenURISchemaIntegrity(uint256)')) == 0xcaae188e
        *
        *   => 0x49846680 ^ 0xcaae188e == 0x832a7e0e
        */
      interfaceID == 0x832a7e0e;   // ERC2477
  }

  /**
   * @dev Creates a registryRecord of an artwork
   * @param isPrimaryIssuer boolean whether the user is a primary issuer
   * @param artistAddress address of the artist contract
   * @param metadataDigest bytes32 of metadata hash
   * @param sig signature
   * @return uint256 tokenId
   */
  function createSRR(
    bool isPrimaryIssuer,
    address artistAddress,
    bytes32 metadataDigest,
    Signature.Sig memory sig
  ) public override validSender(sig) onlyOwner returns (uint256) {
    _createSRR(isPrimaryIssuer, artistAddress, metadataDigest, sig.sender);
  }

  /**
   * @dev Creates a registryRecord of an artwork from LicensedUserLogic contract
   * @param isPrimaryIssuer address of the issuer user contract
   * @param artistAddress address of the artist contract
   * @param metadataDigest bytes32 of metadata hash
   * @return uint256 tokenId
   */
  function createSRRFromLicensedUser(
    bool isPrimaryIssuer,
    address artistAddress,
    bytes32 metadataDigest
  ) public override onlyLicensedUser returns (uint256) {
    return
      _createSRR(isPrimaryIssuer, artistAddress, metadataDigest, msg.sender);
  }

  function createSRRFromBulk(
    bool isPrimaryIssuer,
    address artistAddress,
    bytes32 metadataDigest,
    address issuerAddress
  ) public override onlyBulk returns (uint256) {
    return
      _createSRR(isPrimaryIssuer, artistAddress, metadataDigest, issuerAddress);
  }

  /**
   * @dev Updates the registryRecord
   * @param tokenId uint256 of StartrailRegistryRecordID
   * @param isPrimaryIssuer boolean whether the user is a primary issuer
   * @return uint256 tokenId
   */
  function updateSRRFromLicensedUser(
    uint256 tokenId,
    bool isPrimaryIssuer,
    address artistAddress
  ) public override onlyLicensedUser tokenExists(tokenId) returns (uint256) {
    _addressStorage[tokenId][_SRR][_ARTIST_ADDRESS] = artistAddress;
    require(_saveSRR(tokenId, isPrimaryIssuer), "fail to updateSRR");
    emit UpdateSRR(tokenId, SRR(isPrimaryIssuer, artistAddress, msg.sender));
    return tokenId;
  }

  /**
   * @dev Updates the SRR metadata
   * @param tokenId uint256 of StartrailRegistryRecordID
   * @param metadataDigest bytes32 of the metadata hash
   */
  function updateSRRMetadata(uint256 tokenId, bytes32 metadataDigest)
    external
    override
    onlyLicensedUser
    tokenExists(tokenId)
  {
    _bytes32Storage[tokenId][_SRR][_METADATA_DIGEST] = metadataDigest;
    emit UpdateSRRMetadataDigest(tokenId, metadataDigest);
  }

  /**
   * @dev Approves the given commitment hash to transfer the SRR
   * @param tokenId uint256 ID of the SRR
   * @param commitment bytes32 of the commitment hash
   * @param historyMetadataDigest string of the history metadata digest
   */
  function approveSRRByCommitment(
    uint256 tokenId,
    bytes32 commitment,
    string memory historyMetadataDigest
  ) public override onlySRROwner(tokenId) tokenExists(tokenId) {
    address owner = getSRROwner(tokenId);
    _bytes32Storage[tokenId][_SRR][_COMMITMENT] = commitment;
    _stringStorage[tokenId][_HISTORY][_METADATA_DIGEST] = historyMetadataDigest;
    emit SRRCommitment(owner, commitment, tokenId);
  }

  /**
   * @dev Cancels the current commitment of a given SRR
   * @param tokenId uint256 ID of the SRR
   */
  function cancelSRRCommitment(uint256 tokenId)
    public 
    override
    onlySRROwner(tokenId)
    tokenExists(tokenId)
  {
    _clearSRRCommitment(tokenId);
    emit SRRCommitmentCancelled(tokenId);
  }

  /**
   * @dev Transfers the ownership of a given SRR ID to another address.
   * @param to address to receive the ownership
   * @param reveal bytes32 of the reveal hash value to restore the commitment value
   * @param tokenId uint256 ID of the SRR to be transferred
   */
  function transferSRRByReveal(
    address to,
    bytes32 reveal,
    uint256 tokenId
  ) public override tokenExists(tokenId) {
    bytes32 commitment = keccak256(abi.encodePacked(reveal));
    require(
      commitment == getSRRCommitment(tokenId),
      "Hash of reveal doesn't match"
    );

    address from = getSRROwner(tokenId);
    _historyProvenance(
      tokenId,
      from,
      to,
      _stringStorage[tokenId][_HISTORY][_METADATA_DIGEST]
    );
    _clearSRRCommitment(tokenId);
    _startrailRegistry().transferSRRFrom(from, to, tokenId);
  }

  /**
   * @dev Sets the addresses of the reference
   * @param nameRegistry address of the NameRegistry
   */
  function setNameRegistryAddress(address nameRegistry) public override onlyOwner {
    _addressStorage[SRR_GLOBAL_SLOT][_NAME_REGISTRY][_NAME_REGISTRY] = nameRegistry;
  }

  /**
   * @dev Sets the scheme URI digests of SRR metadata
   * @param digest bytes32 of the scheme URI digests
   */
  function setTokenURISchemaIntegrityDigest(bytes32 digest) public override onlyAdmin {
    _bytes32Storage[SRR_GLOBAL_SLOT][_SRR][_SCHEMA_URI_DIGEST] = digest;
  }

  /**
   * @dev Sets the URI info of the scheme where SRR metadata is saved
   * @param URIPrefix string of the URI prefix of the scheme
   * @param URIPostfix string of the URI postfix of the scheme
   */
  function setTokenURIParts(string memory URIPrefix, string memory URIPostfix)
    public
    override
    onlyAdmin
  {
    _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_PREFIX] = URIPrefix;
    _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_POSTFIX] = URIPostfix;
  }

  /**
   * @dev Gets the registryRecord related with the tokenId
   * @param tokenId uint256 ID of StartrailRegistry
   * @return registryRecord dataset / metadataDigest
   */
  function getSRR(uint256 tokenId)
    public
    override
    view
    tokenExists(tokenId)
    returns (SRR memory registryRecord, bytes32 metadataDigest)
  {
    registryRecord
      .isPrimaryIssuer = _boolStorage[tokenId][_SRR][_IS_PRIMARY_ISSUER];
    registryRecord
      .artistAddress = _addressStorage[tokenId][_SRR][_ARTIST_ADDRESS];
    registryRecord.issuer = _addressStorage[tokenId][_SRR][_ISSUER];
    metadataDigest = _bytes32Storage[tokenId][_SRR][_METADATA_DIGEST];
  }

  /**
   * @dev Gets the current owner address given the token ID
   * @param tokenId uint256 ID of StartrailRegistry
   * @return owner address
   */
  function getSRROwner(uint256 tokenId) public override view returns (address) {
    return _startrailRegistry().getSRROwner(tokenId);
  }

  /**
   * @dev Gets the given commitment hash to transfer the SRR
   * @param tokenId uint256 ID of StartrailRegistry
   * @return commitment hash
   */
  function getSRRCommitment(uint256 tokenId)
    public
    override
    view
    tokenExists(tokenId)
    returns (bytes32)
  {
    return _bytes32Storage[tokenId][_SRR][_COMMITMENT];
  }

  /**
   * @dev Gets the endpoint address of StartrailRegistry
   * @return StartrailRegistry proxy address
   */
  function getStartrailRegistryAddress()
    public
    override
    view
    returns (address)
  {
    return _nameRegistry().get(STARTRAIL_REGISTRY);
  }

  /**
   * @dev Gets the endpoint address of LicensedUserWalletFactory
   * @return LicensedUserWalletFactory proxy address
   */
  function getLicensedUserWalletFactoryAddress()
    public
    override
    view
    returns (address)
  {
    return _nameRegistry().get(LICENSED_USER_WALLET_FACTORY);
  }

  /**
   * @dev Gets the endpoint address of NameRegistry
   * @return NameRegistry proxy address
   */
  function getNameRegistryAddress() public override view returns (address) {
    return
      _addressStorage[SRR_GLOBAL_SLOT][_NAME_REGISTRY][_NAME_REGISTRY];
  }

  /**
   * @dev Gets the scheme digests and hash
   * @param tokenId uint256 ID of StartrailRegistry
   */
  function tokenURISchemaIntegrity(uint256 tokenId)
    external
    override(IRootLogic, ERC2477)
    view
    tokenExists(tokenId)
    returns (bytes memory digest, string memory hashAlgorithm)
  {
    require(
      _addressStorage[tokenId][_SRR][_IS_PRIMARY_ISSUER] != address(0),
      "the tokenId does not exist"
    );
    digest = abi.encodePacked(
      _bytes32Storage[SRR_GLOBAL_SLOT][_SRR][_SCHEMA_URI_DIGEST]
    );
    hashAlgorithm = HASH_ALGORITHM;
  }

  /**
   * @dev Gets the metadata digests of SRR and hash
   * @param tokenId uint256 ID of StartrailRegistry
   */
  function tokenURIIntegrity(uint256 tokenId)
    external
    override(IRootLogic, ERC2477)
    view
    tokenExists(tokenId)
    returns (bytes memory digest, string memory hashAlgorithm)
  {
    digest = abi.encodePacked(_bytes32Storage[tokenId][_SRR][_METADATA_DIGEST]);
    hashAlgorithm = HASH_ALGORITHM;
  }

  /**
   * @dev Gets URI where the matadata is saved
   * @param metadataDigests string of metadata digests
   * @return URI
   */
  function tokenURI(string memory metadataDigests)
    public
    override
    view
    returns (string memory)
  {
    return
      string(
        abi.encodePacked(
          _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_PREFIX],
          metadataDigests,
          _stringStorage[SRR_GLOBAL_SLOT][_SRR][_URI_POSTFIX]
        )
      );
  }

  /**
   * @dev Gets metadata digests
   * @param tokenId uint256 of StartrailRegistryRecordID
   * @return history metadata digest
   */
  function getHistoryMetadataDigest(uint256 tokenId)
    public
    override
    view
    tokenExists(tokenId)
    returns (string memory)
  {
    return _stringStorage[tokenId][_HISTORY][_METADATA_DIGEST];
  }

  /*
   * private functions
   */

  function _createSRR(
    bool isPrimaryIssuer,
    address artistAddress,
    // address platformEOA, // scope out for Day1
    bytes32 metadataDigest,
    address sender
  ) private returns (uint256) {
    uint256 tokenId = _startrailRegistry().mint(sender);
    //_addressStorage[tokenId][_SRR][_PLATFORM_EOA] = platformEOA;
    _addressStorage[tokenId][_SRR][_ISSUER] = sender;
    _addressStorage[tokenId][_SRR][_ARTIST_ADDRESS] = artistAddress;
    _bytes32Storage[tokenId][_SRR][_METADATA_DIGEST] = metadataDigest;
    require(
      _saveSRR(tokenId, isPrimaryIssuer),
      "fail to save Startrail Registry Record"
    );
    emit CreateSRR(
      tokenId,
      SRR(isPrimaryIssuer, artistAddress, sender),
      metadataDigest
    );
    return tokenId;
  }

  /**
   * @dev Saves the registryRecord
   * @param tokenId uint256 of StartrailRegistryRecordID
   * @param isPrimaryIssuer boolean whether the user is a primary issuer
   * @return bool whether the process is successful
   */
  function _saveSRR(uint256 tokenId, bool isPrimaryIssuer)
    private
    returns (bool)
  {
    _boolStorage[tokenId][_SRR][_IS_PRIMARY_ISSUER] = isPrimaryIssuer;
    return true;
  }

  function _isAdmin() private view returns (bool) {
    if(!_nameRegistry().instantiation(msg.sender)) return false;
    LicensedUserLogic logic = LicensedUserLogic(msg.sender);
    return logic.getUserType() == UserType.ADMIN;
  }

  function _exists(uint256 tokenId) private view returns (bool) {
    address owner = getSRROwner(tokenId);
    return owner != address(0);
  }

  function _historyProvenance(
    uint256 tokenId,
    address from,
    address to,
    string memory historyMetadataDigest
  ) private {
    uint256 timestamp = block.timestamp;
    string memory historyMetadataURI = tokenURI(historyMetadataDigest);
    emit Provenance(
      tokenId,
      from,
      to,
      timestamp,
      historyMetadataDigest,
      historyMetadataURI
    );
  }

  function _clearSRRCommitment(uint256 tokenId) private {
    _bytes32Storage[tokenId][_SRR][_COMMITMENT] = "";
    _stringStorage[tokenId][_HISTORY][_METADATA_DIGEST] = "";
  }

  function _startrailRegistry() private view returns (IStartrailRegistry) {
    address startrailRegistryAddress = _nameRegistry().get(STARTRAIL_REGISTRY);
    IStartrailRegistry startrailRegistry = IStartrailRegistry(
      startrailRegistryAddress
    );
    return startrailRegistry;
  }

  function _nameRegistry() private view returns (NameRegistry) {
    return
      NameRegistry(
        _addressStorage[SRR_GLOBAL_SLOT][_NAME_REGISTRY][_NAME_REGISTRY]
      );
  }

  // **************************
  // scope out for Day1
  // **************************

  // /**
  //  * @dev Updates the contract terms (others can be stored as metadata)
  //  * @param tokenId uint256 of StartrailRegistryRecordID
  //  * @param royaltyRate uint8 of the royalty rate
  //  * @param royaltyRateDecimals uint8 of the royalty rate decimals
  //  */
  // function updateContractTerms(
  //   uint256 tokenId,
  //   uint8 royaltyRate,
  //   uint8 royaltyRateDecimals
  // ) external onlyPrimaryIssuer(tokenId) {
  //   _uint8Storage[tokenId][_SRR][_ROYALTY_RATE] = royaltyRate;
  //   _uint8Storage[tokenId][_SRR][_ROYALTY_RATE_DECIMALS] = royaltyRateDecimals;
  //   emit UpdateContractTerms(tokenId, royaltyRate, royaltyRateDecimals);
  // }
}

